mapper.select:
SELECT
-- 级联表的字段 ,liku.liku_name as likuName ,liku.liku_photo as likuPhoto ,liku.liku_file as likuFile ,liku.liku_types as likuTypes ,liku.liku_clicknum as likuClicknum ,liku.shangxia_types as shangxiaTypes ,liku.liku_delete as likuDelete ,liku.liku_content as likuContent ,yonghu.yonghu_uuid_number as yonghuUuidNumber ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.banji_types as banjiTypes ,yonghu.yonghu_email as yonghuEmail
FROM liku_liuyan a left JOIN liku liku ON a.liku_id = liku.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.liku_id = #{params.likuId}
and a.yonghu_id = #{params.yonghuId}
and a.liku_liuyan_text like CONCAT('%',#{params.likuLiuyanText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.liku_id IS NOT NULL
and liku.laoshi_id = #{params.laoshiId}
and liku.liku_name like CONCAT('%',#{params.likuName},'%')
and liku.liku_types = #{params.likuTypes}
and liku.liku_clicknum >= #{params.likuClicknumStart}
and liku.liku_clicknum <= #{params.likuClicknumEnd}
and liku.liku_clicknum = #{params.likuClicknum}
and liku.shangxia_types = #{params.shangxiaTypes}
and liku.liku_delete >= #{params.likuDeleteStart}
and liku.liku_delete <= #{params.likuDeleteEnd}
and liku.liku_delete = #{params.likuDelete}
and liku.liku_content like CONCAT('%',#{params.likuContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.banji_types = #{params.banjiTypes}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
